const axios = require('axios');

// Configuration for fal API
const FAL_API_KEY = process.env.KEYBOARD_FAL_API_KEY;
const FAL_BASE_URL = 'https://queue.fal.run';

if (!FAL_API_KEY) {
  console.error('âŒ FAL_API_KEY not found in environment variables');
  process.exit(1);
}

console.log('ğŸš€ Starting fal image generation with background job polling...');

// Configuration for the image generation
const imageConfig = {
  model: 'fal-ai/flux/schnell', // Using FLUX Schnell for fast generation
  prompt: 'A majestic dragon flying over a mountain landscape at sunset, highly detailed, fantasy art style',
  image_size: 'landscape_4_3',
  num_inference_steps: 4,
  enable_safety_checker: true,
  output_format: 'jpeg'
};

console.log(`ğŸ“ Image Configuration:
- Model: ${imageConfig.model}
- Prompt: ${imageConfig.prompt}
- Size: ${imageConfig.image_size}
- Steps: ${imageConfig.num_inference_steps}
- Format: ${imageConfig.output_format}`);

async function submitImageGeneration() {
  try {
    console.log('ğŸ“¤ Submitting image generation request to fal queue...');

    const response = await axios.post(
      `${FAL_BASE_URL}/${imageConfig.model}/requests`,
      {
        input: {
          prompt: imageConfig.prompt,
          image_size: imageConfig.image_size,
          num_inference_steps: imageConfig.num_inference_steps,
          enable_safety_checker: imageConfig.enable_safety_checker,
          output_format: imageConfig.output_format
        }
      },
      {
        headers: {
          'Authorization': `Key ${FAL_API_KEY}`,
          'Content-Type': 'application/json'
        }
      }
    );

    console.log('âœ… Request submitted successfully!');
    console.log(`ğŸ“‹ Request ID: ${response.data.request_id}`);
    console.log(`ğŸ“Š Status URL: ${response.data.status_url}`);

    return {
      requestId: response.data.request_id,
      statusUrl: response.data.status_url,
      responseUrl: response.data.response_url
    };

  } catch (error) {
    console.error('âŒ Error submitting request:', error.response?.data || error.message);
    throw error;
  }
}

async function pollForResult(requestId, maxAttempts = 60, intervalMs = 2000) {
  console.log(`ğŸ”„ Starting polling for request ${requestId}...`);
  console.log(`â±ï¸  Max attempts: ${maxAttempts}, Interval: ${intervalMs}ms`);

  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    try {
      console.log(`\nğŸ” Polling attempt ${attempt}/${maxAttempts}...`);

      const response = await axios.get(
        `${FAL_BASE_URL}/${imageConfig.model}/requests/${requestId}/status?logs=1`,
        {
          headers: {
            'Authorization': `Key ${FAL_API_KEY}`
          }
        }
      );

      const status = response.data.status;
      console.log(`ğŸ“ˆ Current status: ${status}`);

      // Log any available logs
      if (response.data.logs && response.data.logs.length > 0) {
        console.log('ğŸ“ Logs:');
        response.data.logs.forEach(log => {
          console.log(`   [${log.level}] ${log.message}`);
        });
      }

      if (status === 'COMPLETED') {
        console.log('ğŸ‰ Generation completed!');

        // Get the final result
        const resultResponse = await axios.get(
          `${FAL_BASE_URL}/${imageConfig.model}/requests/${requestId}`,
          {
            headers: {
              'Authorization': `Key ${FAL_API_KEY}`
            }
          }
        );

        return resultResponse.data;
      }

      if (status === 'FAILED') {
        console.error('âŒ Generation failed!');
        console.error('Error details:', response.data);
        throw new Error('Generation failed');
      }

      if (status === 'IN_QUEUE') {
        console.log(`â³ Request is in queue (position: ${response.data.queue_position || 'unknown'})`);
      } else if (status === 'IN_PROGRESS') {
        console.log('ğŸ”„ Generation in progress...');
      }

      // Wait before next poll
      if (attempt < maxAttempts) {
        console.log(`â° Waiting ${intervalMs}ms before next poll...`);
        await new Promise(resolve => setTimeout(resolve, intervalMs));
      }

    } catch (error) {
      console.error(`âŒ Error during polling attempt ${attempt}:`, error.response?.data || error.message);

      if (attempt === maxAttempts) {
        throw error;
      }

      // Wait before retrying
      await new Promise(resolve => setTimeout(resolve, intervalMs));
    }
  }

  throw new Error(`Polling timed out after ${maxAttempts} attempts`);
}

async function verifyImageExists(imageUrl) {
  try {
    console.log('ğŸ” Verifying image exists and is accessible...');

