    console.log(`ğŸ“ Content-Length: ${response.headers['content-length']} bytes`);
    console.log(`ğŸ“‹ Content-Type: ${response.headers['content-type']}`);

    return true;
  } catch (error) {
    console.error('âŒ Error verifying image:', error.message);
    return false;
  }
}

