        response.data.logs.forEach((log, index) => {
          console.log(`   [${index + 1}] [${log.level || 'info'}] ${log.message}`);
        });
      }

      if (status === 'COMPLETED') {
        console.log('ğŸ‰ Generation completed!');

        // KEY FIX: The result data should be in the status response itself!
        console.log('ğŸ“„ Full COMPLETED response:');
        console.log(JSON.stringify(response.data, null, 2));

        // Check if the result data is in the response
        if (response.data.data || response.data.result || response.data.images || response.data.image || response.data.url) {
          return response.data;
        } else {
          console.log('âš ï¸  No result data found in COMPLETED status response');
          console.log('Available keys in response:', Object.keys(response.data));
          return response.data; // Return the full response anyway
        }
      }

      if (status === 'FAILED') {
        console.error('âŒ Generation failed!');
        console.error('Error details:', JSON.stringify(response.data, null, 2));
        throw new Error('Generation failed: ' + JSON.stringify(response.data.error || response.data));
      }

      if (status === 'IN_QUEUE') {
        const position = response.data.queue_position || 'unknown';
        console.log(`â³ Request is in queue (position: ${position})`);
      } else if (status === 'IN_PROGRESS') {
        console.log('ğŸ”„ Generation in progress...');
      } else {
        console.log(`ğŸ“Š Current status: ${status}`);
      }

      // Wait before next poll
      if (attempt < maxAttempts) {
        console.log(`â° Waiting ${intervalMs}ms before next poll...`);
        await new Promise(resolve => setTimeout(resolve, intervalMs));
      }

    } catch (error) {
      console.error(`âŒ Error during polling attempt ${attempt}:`, error.response?.data || error.message);
      console.error('Status:', error.response?.status);

      if (attempt === maxAttempts) {
        throw error;
      }

      // Wait before retrying
      console.log(`â° Waiting ${intervalMs}ms before retry...`);
      await new Promise(resolve => setTimeout(resolve, intervalMs));
    }
  }

  throw new Error(`Polling timed out after ${maxAttempts} attempts`);
}

