  console.log(`ğŸ”„ Starting background job polling for request ${requestId}...`);
  console.log(`â±ï¸  Max attempts: ${maxAttempts}, Interval: ${intervalMs}ms`);

  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    try {
      console.log(`\nğŸ” Background polling attempt ${attempt}/${maxAttempts}...`);

      const pollUrl = `${statusUrl}?logs=1`;

      const response = await axios.get(pollUrl, {
        headers: {
          'Authorization': `Key ${FAL_API_KEY}`
        },
        timeout: 10000
      });

      const status = response.data.status;
      console.log(`ğŸ“ˆ Current status: ${status}`);

      // Log queue position if available
      if (response.data.queue_position !== undefined) {
        console.log(`ğŸš¶ Queue position: ${response.data.queue_position}`);
      }

      // Log processing logs if available
      if (response.data.logs && response.data.logs.length > 0) {
        console.log('ğŸ“ Processing logs:');
        response.data.logs.forEach((log, index) => {
          console.log(`   [${index + 1}] ${log.message || log}`);
        });
      }

      // Show inference time if available
      if (response.data.metrics?.inference_time) {
        console.log(`âš¡ Inference time: ${response.data.metrics.inference_time.toFixed(3)}s`);
      }

      if (status === 'COMPLETED') {
        console.log('ğŸ‰ Background job completed!');

        // COMPREHENSIVE RESULT RETRIEVAL STRATEGY
        console.log('\nğŸ“„ Analyzing COMPLETED response for result data...');
        console.log('Available response keys:', Object.keys(response.data));

        // Method 1: Check if result data is directly in the status response
        if (response.data.data || response.data.result || response.data.images || 
            response.data.image || response.data.url || response.data.outputs) {
          console.log('âœ… Method 1: Result data found in status response!');
          return response.data;
        }

        console.log('ğŸ” Method 1 failed - no direct result data in status response');

        // Method 2: Get result data from status response but look in different locations
        console.log('\nğŸ” Method 2: Checking status response structure...');
        const fullStatusResponse = response.data;
        console.log('Full status response:');
        console.log(JSON.stringify(fullStatusResponse, null, 2));

        // Some fal models return the result in the status response under different keys
        const possibleResultKeys = ['data', 'result', 'output', 'payload', 'content'];
        for (const key of possibleResultKeys) {
          if (fullStatusResponse[key]) {
            console.log(`âœ… Method 2: Found result data under key '${key}'`);
            return { ...fullStatusResponse, resultFrom: `status.${key}` };
          }
        }

        // Method 3: For nano-banana, the result might be distributed across the response
        console.log('\nğŸ” Method 3: Assuming nano-banana has inline result data...');
        // Return the full status response as it might contain the result data inline
        return { ...fullStatusResponse, resultFrom: 'full_status_response' };
      }

      if (status === 'FAILED') {
        console.error('âŒ Background job failed!');
        console.error('Error details:', JSON.stringify(response.data, null, 2));
        throw new Error('Generation failed: ' + JSON.stringify(response.data.error || response.data));
      }

      if (status === 'IN_QUEUE') {
        const position = response.data.queue_position || 'unknown';
        console.log(`â³ Request queued (position: ${position})`);
      } else if (status === 'IN_PROGRESS') {
        console.log('ğŸ”„ Generation in progress...');
      }

      // Wait before next poll
      if (attempt < maxAttempts) {
        console.log(`â° Waiting ${intervalMs}ms before next poll...`);
        await new Promise(resolve => setTimeout(resolve, intervalMs));
      }

    } catch (error) {
      console.error(`âŒ Polling error on attempt ${attempt}:`, error.message);

      if (attempt === maxAttempts) {
        throw error;
      }

      await new Promise(resolve => setTimeout(resolve, intervalMs));
    }
  }

  throw new Error(`Background job polling timed out after ${maxAttempts} attempts`);
}

