function extractImageUrl(result) {
  console.log('\nğŸ” COMPREHENSIVE IMAGE URL EXTRACTION...');
  console.log('Result source:', result.resultFrom || 'unknown');

  // Standard paths
  const possiblePaths = [
    () => result.images?.[0]?.url,
    () => result.image?.url,
    () => result.url,
    () => result.data?.images?.[0]?.url,
    () => result.data?.image?.url,
    () => result.data?.url,
    () => result.outputs?.[0]?.url,
    () => result.data?.outputs?.[0]?.url,
    // nano-banana specific formats
    () => result.result?.url,
    () => result.result?.images?.[0]?.url,
    () => result.output?.url,
    () => result.generated_image?.url,
    () => result.content?.url,
    () => result.payload?.url
  ];

  console.log('ğŸ” Trying standard extraction paths...');
  for (let i = 0; i < possiblePaths.length; i++) {
    try {
      const url = possiblePaths[i]();
      if (url && typeof url === 'string' && url.startsWith('http')) {
        console.log(`âœ… Found image URL at standard path ${i + 1}: ${url}`);
        return url;
      }
    } catch (e) {
      // Continue to next path
    }
  }

  // Deep recursive search for any HTTP URL that looks like an image
  console.log('ğŸ” Performing deep recursive search for image URLs...');
  const searchForUrls = (obj, path = '', depth = 0) => {
    if (depth > 5) return null; // Prevent infinite recursion

    if (typeof obj === 'string' && obj.startsWith('http')) {
      // Check if it looks like an image URL
      const imageExtensions = ['png', 'jpg', 'jpeg', 'webp', 'gif', 'bmp', 'tiff'];
      const hasImageExtension = imageExtensions.some(ext => obj.toLowerCase().includes(ext));
      const hasImageIndicators = obj.includes('image') || obj.includes('media') || obj.includes('cdn');

      if (hasImageExtension || hasImageIndicators || obj.includes('falserverless') || obj.includes('storage.googleapis')) {
        console.log(`ğŸ” Found potential image URL at ${path}: ${obj.substring(0, 100)}${obj.length > 100 ? '...' : ''}`);
        return obj;
      }
    }

    if (typeof obj === 'object' && obj !== null) {
      for (const [key, value] of Object.entries(obj)) {
        const found = searchForUrls(value, path ? `${path}.${key}` : key, depth + 1);
        if (found) return found;
      }
    }
    return null;
  };

  const foundUrl = searchForUrls(result);
  if (foundUrl) {
    console.log(`âœ… Deep search found image URL: ${foundUrl}`);
    return foundUrl;
  }

  // Last resort: check if there are any URLs at all
  console.log('ğŸ” Last resort: looking for any HTTP URLs in result...');
  const allUrls = [];
  const findAllUrls = (obj) => {
    if (typeof obj === 'string' && obj.startsWith('http')) {
      allUrls.push(obj);
    }
    if (typeof obj === 'object' && obj !== null) {
      for (const value of Object.values(obj)) {
        findAllUrls(value);
      }
    }
  };

  findAllUrls(result);

  if (allUrls.length > 0) {
    console.log(`âš ï¸  Found ${allUrls.length} URL(s) but none clearly identified as images:`);
    allUrls.forEach((url, i) => {
      console.log(`   ${i + 1}: ${url.substring(0, 80)}${url.length > 80 ? '...' : ''}`);
    });

    // Return the first URL as a fallback
    console.log('ğŸ”„ Using first URL as fallback...');
    return allUrls[0];
  }

  console.log('âŒ Could not find any image URL in result');
  return null;
}

